from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import ColorSensor, Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
prime_hub = PrimeHub()
color_sensor = ColorSensor(Port.E)
Back_Right_Wheel = Motor(Port.C, Direction.CLOCKWISE)
Back_Left_Wheel = Motor(Port.D, Direction.COUNTERCLOCKWISE)
Front_Left_Gear = Motor(Port.A, Direction.CLOCKWISE)
Front_Right_Gear = Motor(Port.F, Direction.CLOCKWISE)
Drive_Base = DriveBase(Back_Left_Wheel, Back_Right_Wheel, 56, 107)

async def main():
    Drive_Base.use_gyro(True)
    prime_hub.display.number(1)
    await multitask(
        Front_Left_Gear.run_angle(500, 360),
        Front_Right_Gear.run_angle(500, 360),
    )
    await Drive_Base.turn(90)
    await multitask(
        Front_Right_Gear.run_angle(500, 360),
        Front_Left_Gear.run_angle(500, 360),
    )


run_task(main())