from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
prime_hub = PrimeHub()
right_wheel = Motor(Port.C, Direction.COUNTERCLOCKWISE)
left_wheel = Motor(Port.A, Direction.CLOCKWISE)
left_gear = Motor(Port.B, Direction.CLOCKWISE)
drive_base = DriveBase(left_wheel, right_wheel, 56, 114)


# The main program starts here.
drive_base.straight(-250)
for count in range(5):
    left_gear.run_angle(300, -180)
    left_gear.run_angle(300, 180)
