from pybricks.parameters import Direction, Port
from robot_helpers import Robot

robot = Robot(left_port=Port.A, right_port=Port.C, manip_port=Port.B, left_dir=Direction.COUNTERCLOCKWISE, right_dir=Direction.CLOCKWISE, manip_dir=Direction.CLOCKWISE, wheel_diameter=56, axle_track=80)

def Miission_8_and_5():
    robot.straight(332)
    for count in range(4):
        robot.manip.control.limits(acceleration=9000)
        robot.run_manipulator(8000, 580)
        robot.run_manipulator(8000, -580)
    robot.turn(-30)
    robot.straight(400)
    robot.turn(35)
    robot.straight(90)
    robot.turn(-28)
    robot.turn(30)
    robot.straight(-80)

def Mission_9_Push_black_lever():
    robot.turn(-60)
    robot.straight(250)
    robot.turn(-20)
    robot.straight(250)
    robot.turn(-140)
    robot.straight(400)

def Mission_10_Bucket_2():
    robot.straight(-180)
    robot.turn(20)
    robot.run_manipulator(1000, 700)
    robot.run_manipulator(1000, -700)

def Coming_Back_from_10_Bucket():
    robot.drive.settings(straight_speed=700)
    robot.turn(-60)
    robot.straight(350)
    robot.turn(40)
    robot.straight(630)


# The main program starts here.
robot.set_precision_mode()
Miission_8_and_5()
Mission_9_Push_black_lever()
Mission_10_Bucket_2()
Coming_Back_from_10_Bucket()
