from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import wait

# Set up all devices.
right = Motor(Port.C, Direction.CLOCKWISE)
left = Motor(Port.A, Direction.COUNTERCLOCKWISE)
left_gear = Motor(Port.B, Direction.COUNTERCLOCKWISE)
robot = DriveBase(left, right, 56, 98)


# The main program starts here.
robot.use_gyro(True)
robot.straight(615)
robot.turn(-90)
robot.straight(90)
robot.settings(turn_rate=50)
robot.turn(40)
wait(300)
robot.settings(turn_rate=200)
robot.turn(-60)
robot.straight(-85)
robot.settings(turn_rate=180)
robot.turn(20)
robot.settings(straight_speed=100)
robot.straight(75)
left_gear.run_angle(500, 90)
robot.turn(90)
robot.settings(straight_speed=500)
robot.straight(-615)
