from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
right = Motor(Port.C, Direction.CLOCKWISE)
left = Motor(Port.A, Direction.COUNTERCLOCKWISE)
left_gear = Motor(Port.B, Direction.COUNTERCLOCKWISE)
robot = DriveBase(left, right, 56, 98)


# The main program starts here.
robot.use_gyro(True)
robot.settings(straight_acceleration=400)
robot.settings(straight_speed=300)
robot.settings(turn_rate=200)
robot.settings(turn_acceleration=300)
# Drive forward to the location
robot.straight(735)
# turn right at the location
robot.turn(90)
# Drive forward to the location
robot.straight(290)
# turn left
robot.turn(-90)
robot.straight(5)
# LIFT
left_gear.control.limits(acceleration=3000)
left_gear.control.limits(speed=1000)
left_gear.run_angle(300, 720)
# Return to the start
# Describe your program.
robot.turn(90)
left_gear.run_angle(500, -350)
# # Drive back
robot.straight(-350)
# Undo turn right (so turn left)
robot.turn(-90)
# Drive back to start
robot.straight(-735)
left_gear.run_angle(300, -370)
